package com.pry.sublimadoscr.hecho;import android.content.Intent;import android.os.Bundle;import android.view.View;import com.pry.sublimadoscr.databinding.ActivityInicioBinding;import androidx.appcompat.app.AppCompatActivity;public class MenuInicio extends AppCompatActivity {private ActivityInicioBinding binding;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		binding= ActivityInicioBinding.inflate(getLayoutInflater());		setContentView(binding.getRoot());		binding.btnregistrar.setOnClickListener(new View.OnClickListener() {			@Override			public void onClick(View view) {				finish();				startActivity(new Intent(MenuInicio.this, MainLoginAdmindActivity.class));			}		});		binding.btnlogin.setOnClickListener(new View.OnClickListener() {			@Override			public void onClick(View view) {				finish();				startActivity(new Intent(MenuInicio.this, MainLoginActivity.class));			}		});	}}